create or replace view v_stat_logorder as SELECT UUID() as GROUP_KEY, l1.filename as LOG_MAP_FILE,l2.FILENAME as LOG_DETAIL_FILE,l1.LOG_BEGIN_TIMESTAMP as LOG_BEGIN_TIMESTAMP,l2.LOG_END_TIMESTAMP as LOG_END_TIMESTAMP FROM stat_logfile l1 inner join stat_logfile l2 where l1.LOG_END_TIMESTAMP=l2.LOG_BEGIN_TIMESTAMP and l1.FILENAME != l2.FILENAME and l1.LOG_BEGIN_TIMESTAMP <= l2.LOG_BEGIN_TIMESTAMP and l1.GROUP_KEY is null and l2.GROUP_KEY is null;